<?php	session_start();	$bdd = new PDO('mysql:host=localhost;dbname=lisopfr_membre', 'lisopfr', 'hRkrKDkDy6');	$id = $_SESSION['id'];	$rep = $bdd->query("SELECT * FROM membre2 WHERE id=$id");	$donnee = $rep->fetch();	if(!empty($_FILES['image']['name'])){			$extensions = array('.png', '.gif', '.jpg', '.jpeg');		$extension = strrchr($_FILES['image']['name'], '.');		if(!in_array($extension, $extensions))		{			 echo 'Vous devez uploader un fichier de type png, gif, jpg, jpeg.';		}		else		{		if(!empty($donnee['avatar'])){		$avatar=$donnee['avatar'];		unlink("avatars/$avatar");		}			if(preg_match("#.png#i", $_FILES['image']['name'])){			$image=$_SESSION['id'] .'.png';			}			elseif(preg_match("#.gif#i", $_FILES['image']['name'])){			$image=$_SESSION['id'] .'.gif';			}			elseif(preg_match("#.jpg#i", $_FILES['image']['name'])){			$image=$_SESSION['id'] .'.jpg';			}			elseif(preg_match("#.jpeg#i", $_FILES['image']['name'])){			$image=$_SESSION['id'] .'.jpeg';			}			else{			$image='none.png';			}		$source=$_FILES['image']['tmp_name'];		$destination= 'avatars/'.$image;		if($_FILES['image']['error']>0){		echo "erreur lors de transmission de le l'image";		}		if(!move_uploaded_file($source, $destination)) {		echo "erreur lors de déplacement de l'image";		}		$bdd->exec("UPDATE membre2 SET avatar = '$image' WHERE id=$id");		}	}	$lycee=$_POST['lycee'];	$sc=$_POST['sc'];	$mail=$_POST['mail'];	$bdd->exec("UPDATE membre2 SET lycee = '$lycee', sc = '$sc', mail = '$mail' WHERE id=$id");?><script type="text/javascript">		function redirection(page)		  {window.location=page;}		setTimeout('redirection("perso.php")',1);</script>